  void getUser() async {
    UserModel? user = await _prefs.getUser();
    setState(() {
      currentUser = user!;
    });

    if(currentUser.preference!.preferLocation.isEmpty) {
      Future.delayed(const Duration(milliseconds: 100), () {
        Navigator.push(
          context,
          PageTransition(
            type: PageTransitionType.rightToLeft,
            child: SettingScreen(showOtherSettings: false, user: currentUser,),
            curve: Curves.ease,
            duration: const Duration(milliseconds: 500),
          ),
        );
      });
    }
  }

  Future<void> _getCurrentLocation() async {
    bool serviceEnabled;
    LocationPermission permission;

    serviceEnabled = await Geolocator.isLocationServiceEnabled();
    if (!serviceEnabled) {
      return Future.error('Location services are disabled.');
    }

    permission = await Geolocator.checkPermission();
    if (permission == LocationPermission.denied) {
      permission = await Geolocator.requestPermission();
      if (permission == LocationPermission.denied) {
        return Future.error('Location permissions are denied');
      }
    }

    if (permission == LocationPermission.deniedForever) {
      return Future.error('Location permissions are permanently denied, we cannot request permissions.');
    }

    Position position = await Geolocator.getCurrentPosition();
    if(mounted) {
      setState(() {
        _userCurrentPosition = LatLng(position.latitude, position.longitude);
        _isUserLocReady = true;
      });
    }
  }

  double calculateDistance(LatLng start, LatLng end) {
    const Distance distance = Distance();
    return distance(start, end);
  }

  int calculateMatchPreference(PreferenceModel userPrefs, PreferenceModel shopPrefs) {
    int score = 0;

    score += userPrefs.preferFacilities
        .where((pref) => shopPrefs.preferFacilities.contains(pref))
        .length;

    score += userPrefs.preferAtmosphere
        .where((pref) => shopPrefs.preferAtmosphere.contains(pref))
        .length;

    score += userPrefs.preferLocation
        .where((pref) => shopPrefs.preferLocation.contains(pref))
        .length;

    return score;
  }

  void initLocale() async {
    await initializeDateFormatting('id', "");
  }

  String formatDecimalIndonesian(int number) {
    if (number >= 1000000000) {
      double inBillions = number / 1000000000;
      return '${inBillions.toStringAsFixed(1).replaceAll('.', ',')}m';
    } else if (number >= 1000000) {
      double inMillions = number / 1000000;
      return '${inMillions.toStringAsFixed(1).replaceAll('.', ',')}jt';
    } else if (number >= 1000) {
      double inThousands = number / 1000;
      return '${inThousands.toStringAsFixed(1).replaceAll('.', ',')}rb';
    } else {
      return number.toString();
    }
  }

  void _handleDetailScreen(CoffeeShopModel coffeeShop) async {
    final result = await Navigator.push(
      context,
      PageTransition(
        type: PageTransitionType.rightToLeft,
        child: DetailCoffeeScreen(coffeeShop: coffeeShop, user: currentUser,),
        curve: Curves.ease,
      ),
    );

    await _firestore.addCoffeeShopClicks(coffeeShop);

    if (result != null && result is CoffeeShopModel) {
      if(widget.showCoffeeRoute != null) {
        widget.showCoffeeRoute!(coffeeShop);
      }
    }
  }

