  @override
  Widget build(BuildContext context) {
    return SafeArea(
      child: ClipRRect(
        child: SingleChildScrollView(
          padding: const EdgeInsets.symmetric(
            horizontal: 20,
          ),
          clipBehavior: Clip.none,
          physics: const BouncingScrollPhysics(),
          scrollDirection: Axis.vertical,
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              const SizedBox(height: 20,),
              Text(
                "Beranda",
                style: Theme.of(context).textTheme.headlineSmall?.copyWith(
                  color: primaryColor,
                ),
              ),
              Text(
                "Hai ${currentUser.username}, bagaimana perasaanmu hari ini?",
                style: Theme.of(context).textTheme.bodySmall,
              ),
              const SizedBox(height: 20,),
              Text(
                "Rekomendasi",
                style: Theme.of(context).textTheme.bodyMedium?.copyWith(
                  fontWeight: FontWeight.bold,
                ),
              ),
              const SizedBox(height: 12,),
              StreamBuilder<QuerySnapshot>(
                stream: _firestore.getRecommendationShopStream(),
                builder: (context, snapshot) {
                  if (!snapshot.hasData) {
                    return const Center(
                      child: CircularProgressIndicator(
                        color: primaryColor,
                      ),
                    );
                  }

                  List<CoffeeShopModel> places = snapshot.data!.docs.map((doc) {
                    return CoffeeShopModel.fromJson(doc.data() as Map<String, dynamic>);
                  }).toList();

                  places.sort((a, b) {
                    int scoreA = calculateMatchPreference(currentUser.preference!, a.category!);
                    int scoreB = calculateMatchPreference(currentUser.preference!, b.category!);
                    return scoreB.compareTo(scoreA); // Descending order
                  });

                  places = places.take(10).toList();

                  return SingleChildScrollView(
                    clipBehavior: Clip.none,
                    physics: const BouncingScrollPhysics(),
                    scrollDirection: Axis.horizontal,
                    child: Row(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: places.map((place) {
                        return Padding(
                          padding: const EdgeInsets.only(right: 8.0),
                          child: GestureDetector(
                            onTap: () {
                              _handleDetailScreen(place);
                            },
                            child: CustomCardPlace(
                              imageURL: place.imageURL,
                              title: place.name,
                              subTitle: place.addressShort,
                            ),
                          ),
                        );
                      }).toList(),
                    ),
                  );
                },
              ),
              const SizedBox(height: 20,),
              _isUserLocReady ? Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Text(
                    "Terdekat",
                    style: Theme.of(context).textTheme.bodyMedium?.copyWith(
                      fontWeight: FontWeight.bold,
                    ),
                  ),
                  const SizedBox(height: 12,),
                  StreamBuilder<QuerySnapshot>(
                    stream: _firestore.getNearestShopStream(),
                    builder: (context, snapshot) {
                      if (!snapshot.hasData) {
                        return const Center(
                          child: CircularProgressIndicator(
                            color: primaryColor,
                          ),
                        );
                      }

                      List<CoffeeShopModel> places = snapshot.data!.docs.map((doc) {
                        return CoffeeShopModel.fromJson(doc.data() as Map<String, dynamic>);
                      }).toList();

                      places.sort((a, b) {
                        LatLng posA = LatLng(a.latitude, a.longitude);
                        LatLng posB = LatLng(b.latitude, b.longitude);
                        double distanceA = calculateDistance(_userCurrentPosition!, posA);
                        double distanceB = calculateDistance(_userCurrentPosition!, posB);
                        return distanceA.compareTo(distanceB);
                      });

                      places = places.take(10).toList();

                      return SingleChildScrollView(
                        clipBehavior: Clip.none,
                        physics: const BouncingScrollPhysics(),
                        scrollDirection: Axis.horizontal,
                        child: Row(
                          children: places.map((place) {
                            LatLng placePosition = LatLng(place.latitude, place.longitude);
                            double distance = calculateDistance(_userCurrentPosition!, placePosition);
                            String distanceText = distance < 1000 ? "${distance.toStringAsFixed(0)} m" : "${(distance / 1000).toStringAsFixed(2)} km";

                            return Padding(
                              padding: const EdgeInsets.only(right: 8.0),
                              child: GestureDetector(
                                onTap: () {
                                  _handleDetailScreen(place);
                                },
                                child: CustomCardPlace(
                                  imageURL: place.imageURL,
                                  title: place.name,
                                  subTitle: distanceText,
                                ),
                              ),
                            );
                          }).toList(),
                        ),
                      );
                    },
                  ),
                  const SizedBox(height: 20,),
                ],
              ) : const SizedBox(),
              Text(
                "Sedang Populer",
                style: Theme.of(context).textTheme.bodyMedium?.copyWith(
                  fontWeight: FontWeight.bold,
                ),
              ),
              const SizedBox(height: 12,),
              StreamBuilder<QuerySnapshot>(
                stream: _firestore.getPopularShopStream(),
                builder: (context, snapshot) {
                  if (!snapshot.hasData) {
                    return const Center(
                      child: CircularProgressIndicator(
                        color: primaryColor,
                      ),
                    );
                  }

                  List<CoffeeShopModel> places = snapshot.data!.docs.map((doc) {
                    return CoffeeShopModel.fromJson(doc.data() as Map<String, dynamic>);
                  }).toList();

                  places = places.take(10).toList();

                  return SingleChildScrollView(
                    clipBehavior: Clip.none,
                    physics: const BouncingScrollPhysics(),
                    scrollDirection: Axis.horizontal,
                    child: Row(
                      children: places.map((place) {
                        return Padding(
                          padding: const EdgeInsets.only(right: 8.0),
                          child: GestureDetector(
                            onTap: () {
                              _handleDetailScreen(place);
                            },
                            child: CustomCardPlace(
                              imageURL: place.imageURL,
                              title: place.name,
                              subTitle: "Dilihat ${formatDecimalIndonesian(place.clicks)}",
                            ),
                          ),
                        );
                      }).toList(),
                    ),
                  );
                },
              ),
              const SizedBox(height: 96,),
            ],
          ),
        ),
      ),
    );
  }
}
